import React, { Component } from "react";
import Chart from "react-apexcharts";
import Header from '../includes/header'
import ProductApi from '../data/ProductApi';
import _ from 'lodash';

class ColumnChart extends Component {
  constructor(props) {
    super(props);

    this.state = {
        isLoaded: true,
        data: {},
      options: {
        chart: {
          id: "basic-bar"
        },
        xaxis: {
          categories: [1991, 1992, 1993, 1994, 1995, 1996, 1997, 1998, 1999]
        }
      },
      series: [
        {
          name: "series-1",
          data: [30, 40, 45, 50, 49, 60, 70, 91]
        }
      ]
    };
  }

  componentDidMount() {
    ProductApi.getProductViews(productViews => {
        const grouped = _(productViews).groupBy('productName').value()
        console.log("Data:::",Object.keys(grouped), productViews)
        const series = [
            {
              name: productViews.name,
              data: [productViews.views]
            }
          ]
        this.setState({data: productViews, series})
    })
    setTimeout(() => {
        this.setState({ isLoaded: false })
    }, 1000);
  }
    render() {
        return (
            <div>
                <Header />
                {this.state.isLoaded ? <div><center><div className="loading-spinner"></div></center></div> :
                    <div className="mixed-chart">
                        <Chart
                            options={this.state.options}
                            series={this.state.series}
                            type="bar"
                            width="500"
                        />
                    </div>
                }
            </div>
        );
    }
}

export default ColumnChart;
